package parse

import (
	"strings"

	"github.com/jung-kurt/gofpdf"
	"github.com/tcd/md2pdf/internal/render"
	"golang.org/x/net/html"
)

// HTML renders a PDF from an html string generated by blackfriday
func HTML(inputHTML, outputPath string) error {

	doc := strings.NewReader(inputHTML)
	tokenizer := html.NewTokenizer(doc)
	pdf := gofpdf.New("P", "mm", "Letter", "")
	render.Setup(pdf)

	for {
		tt := tokenizer.Next()

		if tt == html.ErrorToken {
			break // End of document
		}

		if tt == html.SelfClosingTagToken {
			T1 := tokenizer.Token()

			if T1.Data == "hr" {
				render.HR(pdf)
			}
			if T1.Data == "br" {
				// decide how to deal with these?
			}
			if T1.Data == "img" {
				parseImg(pdf, T1)
			}
		}

		if tt == html.StartTagToken {
			T1 := tokenizer.Token()

			if T1.Data == "h1" {
				parseH1(pdf, tokenizer)
			}
			if T1.Data == "h2" {
				parseH2(pdf, tokenizer)
			}
			if T1.Data == "h3" {
				parseH3(pdf, tokenizer)
			}
			if T1.Data == "h4" {
				parseH4(pdf, tokenizer)
			}
			if T1.Data == "h5" {
				parseH5(pdf, tokenizer)
			}
			if T1.Data == "h6" {
				parseH6(pdf, tokenizer)
			}
			if T1.Data == "pre" {
				parsePre(pdf, tokenizer)
			}
			if T1.Data == "p" {
				parseP(pdf, tokenizer)
			}
			if T1.Data == "blockquote" {
				// parseBlockquote(pdf, tokenizer)
			}
			if T1.Data == "ol" || T1.Data == "ul" {
				// parseList(pdf, tokenizer)
			}
			if T1.Data == "dl" {
				// Deal with definition lists?
			}
			if T1.Data == "table" {
				// parseTable(pdf, tokenizer)
			}
		}
	}

	return pdf.OutputFileAndClose(outputPath)
}
